INSERT INTO PUBLIC.CHURCH_CONTENT(CHURCH_ID, PAGE_ID, LOCALE, TITLE, BODY) VALUES(5, 'minister', 'en', 'CEC in Livingston - Minister Introduction', STRINGDECODE('<h1>Pastor Stephen Lim</h1>\r\n<h2></h2>\r\n<h2></h2>\r\n<h2>Current Ministry:</h2>\r\n<p>\r\nPastor of Christian Evangelical Church in Livingston<br />\r\nProject Coordinator for EMSI property<br />\r\nAdministrative duties at EMSI headquarter\r\n</p>\r\n<h2>Ministry experience prior to pastorate:</h2>\r\n<p>\r\nTaught Sunday School classes at all levels<br />\r\nPreached at worship service<br />\r\nIn-charge of caring/hospitality/visitation ministry<br />\r\nIn-charge of juniors and family fellowships<br />\r\nTaught at juniors, youth and family fellowships<br />\r\nServed in the choir<br />\r\nServed as co-worker and deacon\r\n</p>\r\n<h2>Work experience prior to pastorate:</h2>\r\n<p>\r\nCo-owned a photo lab<br />\r\nManaged a real estate company<br />\r\nAdministrative and personnel manager of a manufacturing company<br />\r\nBranch manager of a merchant bank<br />\r\nBank building administrator<br />\r\nLoan processor, financial analyst, credit investigator, appraiser of a commercial bank<br />\r\nWholesale/retail salesman and parts ordering clerk of a GM franchised dealer \r\n</p>\r\n<h2>Educational attainment:</h2>\r\n<p>\r\nMaster of Divinity (M. Div.)<br />\r\nMaster of Arts in Biblical Studies (incomplete)<br />\r\nMaster in Business Administration (M. B. A..)<br />\r\nBachelor of Science in Business (B. S. B.) \r\n</p>\r\n'));             
