INSERT INTO PUBLIC.CHURCH_CONTENT(CHURCH_ID, PAGE_ID, LOCALE, TITLE, BODY) VALUES(8, 'news', 'en', 'CEMC in Towaco - Latest News', STRINGDECODE('<p>\r\n7/13/13\r\n</p>\r\n<p style=\"text-indent: -23.799999237060547px\">\r\n1.<span class=\"Apple-tab-span\" style=\"white-space: pre\">\t</span>Caleb Fellowship will meet today at 12:30 pm on the 2nd floor.\r\n</p>\r\n<p style=\"text-indent: -23.799999237060547px\">\r\n2.<span class=\"Apple-tab-span\" style=\"white-space: pre\">\t</span>Attention VBS Volunteers. Please come to the prayer meeting today at 12:30 pm in the Children\u2019s Worship Room.\u00a0\r\n</p>\r\n<p style=\"text-indent: -23.799999237060547px\">\r\n3.<span class=\"Apple-tab-span\" style=\"white-space: pre\">\t</span>English Bible Study will meet on Monday at 7:45 pm.\u00a0\r\n</p>\r\n<p style=\"text-indent: -23.799999237060547px\">\r\n4.<span class=\"Apple-tab-span\" style=\"white-space: pre\">\t</span>Mandarin Bible Study will meet on Wed (7/17) 7:30pm.\u00a0\r\n</p>\r\n<p style=\"text-indent: -23.799999237060547px\">\r\n5.<span class=\"Apple-tab-span\" style=\"white-space: pre\">\t</span>Please reserve the date for EMSI 45th Anniversary Thanksgiving Service which will take place on Aug.11 (Sunday) in Towaco church.\u00a0\r\n</p>\r\n<p style=\"text-indent: -23.799999237060547px\">\r\n6.<span class=\"Apple-tab-span\" style=\"white-space: pre\">\t</span>Sports Day will be held on Sat. 8/17 from 9am to 5pm. Sign up sheets for various sport competitions are now posted on the bulletin.\r\n</p>\r\n<p style=\"text-indent: -23.799999237060547px\">\r\n7.<span class=\"Apple-tab-span\" style=\"white-space: pre\">\t</span>Please pray for Pastor Yang\u2019s upcoming chemo &amp; radiation therapy to be effective and for his next surgery to go smoothly.\u00a0\r\n</p>\r\n<p style=\"text-indent: -23.799999237060547px\">\r\n&nbsp;\r\n</p>\r\n<p style=\"text-indent: -23.799999237060547px\">\r\nThis week\u2019s Bible verse:\u00a0\r\n</p>\r\n<p style=\"text-indent: -23.79