INSERT INTO PUBLIC.CHURCH_CONTENT(CHURCH_ID, PAGE_ID, LOCALE, TITLE, BODY) VALUES(11, 'testimonies', 'zh', STRINGDECODE('\u820a\u91d1\u5c71\u751f\u547d\u5802 - \u898b\u8b49'), STRINGDECODE('<p>\r\n<span style=\"font-size: medium\">1.\u4ece\u6c89\u6ca6\u5230\u65b0\u751f</span>\r\n</p>\r\n<p>\r\n\u5e74\u5c11\u5f53\u5175\u6218\u65f6\u5931\u5b66\uff0c\u65e0\u6587\u5316\u7a0b\u5ea6\u5e95\u7684\u6211\uff0c\u53ea\u77e5\u654c\u4e0e\u6211\uff0c\u80dc\u4e0e\u8d25\uff0c\u6b63\u662f\u201c\u4e00\u751f\u4ec7\u4e0e\u6068\u201d\u90aa\u6076\u7f6a\u6ee1\u8eab\uff0c\u4eba\u5728\u4e0d\u5f52\u8def\uff0c\u4e0d\u8bc6\u4fe1\u4e3b\u5fc3\u3002\u201c\u4ece\u6297\u62d2\u5b97\u6559\u4fe1\u4ef0\uff0c\u76f4\u81f3\u6709\u4e00\u5929\u660e\u767d\u4ec0\u4e48\u662f[\u7f6a]\u624d\u8ba4\u8bc6\u5230\u4e3b\uff0c\u61c2\u5f97\u6094\u6539\uff0c\u85c9\u7740\u4e3b\u8036\u7a23\u57fa\u7763\u7684\u5b9d\u8840\u66ff\u6211\u4eec\u8d4e\u7f6a\u3002\u4fe1\u4e3b\u4ee5\u540e\uff0c\u4eba\u751f\u4ece\u9ed1\u6697\u89c1\u5230\u5149\u660e\uff0c\u751f\u6d3b\u4ece\u6c89\u6ca6\u5f97\u5230\u65b0\u751f\uff0c\u611f\u8c22\u4e3b\u7684\u6551\u6069\u8d50\u6211\u65b0\u7684\u751f\u547d\u3002\u201d\u82e5\u6709\u4eba\u5728\u57fa\u7763\u91cc\u4ed6\u5c31\u662f\u65b0\u9020\u7684\u4eba\uff0c\u65e7\u4e8b\u5df2\u8fc7\u90fd\u53d8\u6210\u65b0\u7684\u4e86\uff01\u201c\uff08\u6797\u540e5\uff1a17\uff09.\u4ece\u6b64\u201d\u518d\u4e0d\u56de\u9996\u8bdd\u5f53\u5e74\uff0c\u65e0\u8fb9\u6068\u6094\u82e6\u96be\u582a\uff0c\u798f\u97f3\u826f\u836f\u5bf9\u75c7\u4e0b\uff0c\u63a5\u53d7\u6551\u6069\u5165\u751f\u5929\uff01\u201d \u8033\u8fb9\u542c\u5230\u6b4c\u58f0\u201c\u8ddf\u7740\u4e3b\u5411\u524d\u8d70\uff0c\u4e00\u53bb\u4e0d\u56de\u5934\uff0c\u8ddf\u7740\u4e3b\u5411\u524d\u8d70\uff0c\u4e3b\u5c31\u5728\u524d\u5934\u201c\r\n</p>\r\n<p>\r\n<img src=\"/emsi/files/guan008.jpg\" height=\"98\" width=\"100\" title=\"BroKuan\" alt=\"BroKuan\" /><br />\r\n\u4e09\u85e9\u5e02\u751f\u547d\u5802 \u5173\u4f1f\u5f1f\u5144 \r\n</p>\r\n<p>\r\n&nbsp;\r\n</p>\r\n<p>\r\n<b style=\