INSERT INTO PUBLIC.CHURCH_CONTENT(CHURCH_ID, PAGE_ID, LOCALE, TITLE, BODY) VALUES(21, 'minister', 'en', 'CEC in Den Haag - Minister Introduction', STRINGDECODE('<p class=\"MsoNormal\" style=\"text-align: justify\">\r\nRev. Tze Mui has been a faithful deacon and coworker for our Evangelical Mission church in Chinatown for many years. After called by God into fulltime ministry, he gave up his secular work, and attended our Evangelical Bible Institute. After successfully graduated, he was assigned to our Evangelical Mission church in Towaco, NJ, overseeing the English ministry.\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align: justify\">\r\nRev. Mui was born in Hong Kong. He is married with 2 children. In additional to being fluent in Cantonese, he also speaks English and Mandarin. He is much gifted. Aside from apt to teach and preach, he is especially skilled in interpreting the Bible. \r\n</p>\r\n'));          
