INSERT INTO PUBLIC.CHURCH_CONTENT(CHURCH_ID, PAGE_ID, LOCALE, TITLE, BODY) VALUES(27, 'minister', 'en', 'CEC in Madrid - Pastor Introduction', STRINGDECODE('<p>\r\n<span style=\"font-size: small; font-family: ''times new roman'', times\" class=\"Apple-style-span\">\r\n(This page is in spanish - \u8fd9\u9875\u662f\u7528\u897f\u73ed\u7259\u8bed\u5199\u7684 \uff0d Est\u00e1 p\u00e1gina est\u00e1 en espa\u00f1ol)\r\n</span>\r\n</p>\r\n<div class=\"content\" style=\"margin-top: 0.6em; margin-right: 0px; margin-bottom: 0.6em; margin-left: 0px\">\r\n<p style=\"margin-top: 0.6em; margin-right: 0px; margin-bottom: 1.2em; margin-left: 0px; padding: 0px\">\r\n<span style=\"font-size: small; font-family: ''times new roman'', times\" class=\"Apple-style-span\"><span class=\"Apple-style-span\">\u00a0La pastora Yueh-Yun Li (</span>\u674e\u6708\u96f2<span class=\"Apple-style-span\">) naci\u00f3 en Taipei (Taiwan). </span></span>\r\n</p>\r\n<p style=\"margin-top: 0.6em; margin-right: 0px; margin-bottom: 1.2em; margin-left: 0px; padding: 0px\">\r\n<span style=\"font-size: small; font-family: ''times new roman'', times\" class=\"Apple-style-span\"><span class=\"Apple-style-span\">\u00a0En la iglesia de\u00a0<span class=\"Apple-style-span\" style=\"font-family: \u65b0\u7d30\u660e\u9ad4\">\u96d9\u57ce\u8857\u79ae\u62dc\u5802 (actual iglesia de\u00a0</span><span class=\"Apple-style-span\" style=\"font-family: \u65b0\u7d30\u660e\u9ad4\">\u53f0\u5317\u65b0\u751f\u57fa\u7763\u6559\u6703</span><span class=\"Apple-style-span\" style=\"font-family: \u65b0\u7d30\u660e\u9ad4\">)</span>\u00a0crey\u00f3 en el Se\u00f1or y en 1964 se bautiz\u00f3 en la misma iglesia (ya con el nombre de\u00a0<span class=\"Apple-style-span\" style=\"font-family: \u65b0\u7d30\u660e\u9ad4\">\u53f0\u5317\u65b0\u751f\u57fa\u7763\u6559\u6703).</span></span>\r\n</span>\r\n</p>\r\n<ul>\r\n\t<li><span style=\"font-size: small; font-family: ''times new roman'', times\" class=\"Apple-style-span\">1988: Fue llamada para su total dedicaci\u00f3n al Se\u00f1or. Estudi\u00f3 en la facultad de teolog\u00eda de\u00a0<span class=\"Apple-style-span\" style=\"line-height: 26px; font-family: \u65b0\u7d30\u660e\u9ad4\">\u53f0\u5317\u62ff\u6492\u52d2\u4eba\u6703\u795e\u5b78\u9662 de Taipei.</span></span></li>\r\n\t<li><span style=\"font-size: small; font-family: ''times new roman'', times\" class=\"Apple-style-span\"><span class=\"Apple-style-span\" style=\"font-family: \u65b0\u7d30\u660e\u9ad4\"><span class=\"Apple-style-span\" style=\"line-height: 26px\">1991: Estudia en la facultad de teolog\u00eda de\u00a0</span></span><span class=\"Apple-style-span\" style=\"line-height: 26px; font-family: \u65b0\u7d30\u660e\u9ad4\">\u7f8e\u570b\u570b\u969b\u798f\u97f3\u795e\u5b78\u9662 (EMSI) en EEUU. En 1994 se gradua del M\u00e1ster de Investigaci\u00f3n de Estudios B\u00edblicos.</span></span></li>\r\n\t<li><span class=\"Apple-style-span\" style=\"line-height: 26px; font-family: \u65b0\u7d30\u660e\u9ad4\"><span style=\"font-size: small; font-family: ''times new roman'', times\" class=\"Apple-style-span\">1992-1993: Es enviada como misionera a Osaka (Jap\u00f3n) durante medio a\u00f1o.</span></span></li>\r\n\t<li><span class=\"Apple-style-span\" style=\"font-family: \u65b0\u7d30\u660e\u9ad4\"><span class=\"Apple-style-span\" style=\"line-height: 26px\"><span style=\"font-size: small; font-family: ''times new roman'', times\" class=\"Apple-style-span\">Noviembre, 1994: Es enviada como misionera a Espa\u00f1a durante dos meses, estudiando la evangelizaci\u00f3n en ese pa\u00eds donde m\u00e1s tarde se registra fundando la primera rama de EMSI en Europa: CEM in Spain (MEC en Espa\u00f1a).</span></span></span></li>\r\n\t<li><span class=\"Apple-style-span\" style=\"font-family: \u65b0\u7d30\u660e\u9ad4\"><span class=\"Apple-style-span\" style=\"line-height: 26px\"><span style=\"font-size: small; font-family: ''times new roman'', times\" class=\"Apple-style-span\">Nov., 1995: Se instala de manera definitiva en Espa\u00f1a para la Misi\u00f3n Evang\u00e9lica China Espa\u00f1ola. En diciembre comienzan las clases del estudio de la Biblia.</span></span></span></li>\r\n\t<li><span class=\"Apple-style-span\" style=\"font-family: \u65b0\u7d30\u660e\u9ad4\"><span class=\"Apple-style-span\" style=\"line-height: 26px\"><span style=\"font-size: small; font-family: ''times new roman'', times\" class=\"Apple-style-span\">Marzo, 1996: Se funda la escuela de la EMSI en Espa\u00f1a. Comienza con el curso: &quot;Sistema de ampliaci\u00f3n de la teolog\u00eda para los creyentes laicos&quot;.</span></span></span></li>\r\n\t<li><span class=\"Apple-style-span\" style=\"font-family: \u65b0\u7d30\u660e\u9ad4\"><span class=\"Apple-style-span\" style=\"line-height: 26px\"><span style=\"font-size: small; font-family: ''times new roman'', times\" class=\"Apple-style-span\">7 Abril, 1996: Funda la primera iglesia en Madrid, capital de Espa\u00f1a, con el nombre de: &quot;<span class=\"Apple-style-span\" style=\"line-height: normal; color: #000000\">\u99ac\u5fb7\u91cc\u57fa\u7763\u6559\u751f\u547d\u5802</span>&quot;.</span></span></span></li>\r\n\t<li><span class=\"Apple-style-span\" style=\"font-family: \u65b0\u7d30\u660e\u9ad4\"><span class=\"Apple-style-span\" style=\"line-height: 26px\"><span style=\"font-size: small; font-family: ''times new roman'', times\" class=\"Apple-style-span\">9 Mayo. 2001: Compra del lugar de la actual ubicaci\u00f3n de la iglesia.</span></span></span></li>\r\n\t<li><span class=\"Apple-style-span\" style=\"font-family: \u65b0\u7d30\u660e\u9ad4\"><span class=\"Apple-style-span\" style=\"line-height: 26px\"><span style=\"font-size: small; font-family: ''times new roman'', times\" class=\"Apple-style-span\">10 Marzo, 2002: Consagraci\u00f3n de la iglesia en la celebraci\u00f3n del 6\u00ba (sexto) aniversario de la iglesia de Madrid.</span></span></span></li>\r\n\t<li><span class=\"Apple-style-span\" style=\"font-family: \u65b0\u7d30\u660e\u9ad4\"><span class=\"Apple-style-span\" style=\"line-height: 26px\"><span style=\"font-size: small; font-family: ''times new roman'', times\" class=\"Apple-style-span\">18 Abril, 2004: La pastora Yueh-Yun Li (\u674e\u4f20\u9053) es ordenada reverenda: Reverenda Yueh-Yun Li (\u674e<span class=\"Apple-style-span\" style=\"line-height: normal; font-family: Arial, MingLiU, Helvetica, san-serif\">\u6708\u96f2\u7267\u5e08</span>)</span></span></span></li>\r\n\t<li><span style=\"font-size: small; font-family: ''times new roman'', times\" class=\"Apple-style-span\"><span class=\"Apple-style-span\" style=\"font-family: \u65b0\u7d30\u660e\u9ad4\"><span class=\"Apple-style-span\" style=\"line-height: 26px\">Actualmente es la encargada de la evangelizaci\u00f3n de la EMSI en Espa\u00f1a. Tambi\u00e9n tiene a su cuidado la iglesia de Madrid (</span></span><span class=\"Apple-style-span\" style=\"line-height: 26px; font-family: \u65b0\u7d30\u660e\u9ad4\">\u99ac\u5fb7\u91cc\u57fa\u7763\u6559\u751f\u547d\u5802).</span></span></li>\r\n</ul>\r\n<p>\r\n&nbsp;\r\n</p>\r\n<div>\r\n<span style=\"font-size: 10pt; font-family: \u65b0\u7d30\u660e\u9ad4\"><span style=\"color: #000000\"><br />\r\n</span></span>\r\n</div>\r\n</div>\r\n<div class=\"clear-block clear\" style=\"display: block\">\r\n<div class=\"meta\">\r\n</div>\r\n</div>\r\n<p>\r\n&nbsp;\r\n</p>\r\n'));               
INSERT INTO PUBLIC.CHURCH_CONTENT(CHURCH_ID, PAGE_ID, LOCALE, TITLE, BODY) VALUES(27, 'minister', 'zh', STRINGDECODE('\u99ac\u5fb7\u91cc\u751f\u547d\u5802 - \u7267\u5e2b\u4ecb\u7d39'), STRINGDECODE('<span style=\"font-family: \u65b0\u7d30\u660e\u9ad4; color: #333333; font-size: 10pt\">\u674e\u6708\u96f2\u7267\u5e2b<span lang=\"EN-US\">, \u53f0\u7063\u53f0\u5317\u5e02\u51fa\u751f, \u5728\u96d9\u57ce\u8857\u79ae\u62dc\u5802(\u53f0\u5317\u65b0\u751f\u57fa\u7763\u6559\u6703\u524d\u8eab)\u4fe1\u4e3b, 1964\u5e74\u65bc\u53f0\u5317\u65b0\u751f\u57fa\u7763\u6559\u6703\u53d7\u6d17\u3002<o:p></o:p></span></span> \r\n<h2 style=\"line-height: 20pt; text-indent: -20.4pt; margin: 0cm 0cm 0pt 20.4pt; background: white; tab-stops: 285.3pt\"><span style=\"font-family: \u65b0\u7d30\u660e\u9ad4; color: #333333; font-size: 10pt\" lang=\"EN-US\">1988\u5e74\u8499\u53ec\u5168\u6642\u9593\u5949\u737b, \u5c31\u8b80\u53f0\u5317\u62ff\u6492\u52d2\u4eba\u6703\u795e\u5b78\u9662\u3002<o:p></o:p></span></h2>\r\n<h2 style=\"line-height: 20pt; text-indent: -20.4pt; margin: 0cm 0cm 0pt 20.4pt; background: white; tab-stops: 285.3pt\"><span style=\"font-family: \u65b0\u7d30\u660e\u9ad4; color: #333333; font-size: 10pt\" lang=\"EN-US\">1991\u5e74\u81f3\u7f8e\u570b\u570b\u969b\u798f\u97f3\u795e\u5b78\u9662\u5c31\u8b80, 1994\u5e74\u8056\u7d93\u7814\u7a76\u78a9\u58eb\u7562\u696d\u3002<o:p></o:p></span></h2>\r\n<h2 style=\"line-height: 20pt; text-indent: -20.4pt; margin: 0cm 0cm 0pt 20.4pt; background: white; tab-stops: 285.3pt\"><span style=\"font-family: \u65b0\u7d30\u660e\u9ad4; color: #333333; font-size: 10pt\" lang=\"EN-US\">1992-1993\u5e74\u795e\u5b78\u9662\u5dee\u6d3e\u81f3\u65e5\u672c\u5927\u962a\u5ba3\u6559\u534a\u5e74\u3002<o:p></o:p></span></h2>\r\n<h2 style=\"line-height: 20pt; text-indent: -20.4pt; margin: 0cm 0cm 0pt 20.4pt; background: white; tab-stops: 285.3pt\"><span style=\"font-family: \u65b0\u7d30\u660e\u9ad4; color: #333333; font-size: 10pt\" lang=\"EN-US\">1994\u5e7411\u6708\u795e\u5b7