INSERT INTO PUBLIC.CHURCH_CONTENT(CHURCH_ID, PAGE_ID, LOCALE, TITLE, BODY) VALUES(39, 'testimonies', 'zh', STRINGDECODE('\u5df4\u9ece\u751f\u547d\u5802 - \u898b\u8b49'), STRINGDECODE('<p>\r\n<strong><span style=\"font-size: medium\">\u8036\u7a23\u662f\u6211\u5bb6\u7684\u4e3b</span></strong> \r\n</p>\r\n<p>\r\n\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0 2006\u5e745\u6708\u662f\u6211\u96be\u5fd8\u7684\uff0c\u57285\u6708\u6211\u51b3\u5fd7\u4fe1\u4e3b\uff0c\u4e5f\u662f\u57285\u6708\uff0c\u6211\u5728\u5df4\u9ece\u751f\u547d\u5802\u63a5\u53d7\u6d17\u793c\uff0c\u6210\u4e3a\u57fa\u7763\u5f92\u3002\u5f53\u7267\u5e08\u4e3a\u6211\u6d17\u793c\u65f6\u95ee\u6211\u201c\u662f\u5426\u627f\u8ba4\u81ea\u5df1\u7684\u7f6a\uff0c\u613f\u610f\u5c06\u8036\u7a23\u57fa\u7763\u63a5\u5230\u6211\u7684\u5fc3\u91cc\uff0c\u505a\u6211\u751f\u547d\u7684\u6551\u4e3b\u201d\u65f6\u3002\u6211\u4ece\u5fc3\u91cc\u8bf4\uff1a\u201c\u4e3b\u554a\uff01\u6211\u613f\u610f\uff0c\u4f60\u6551\u6211\u3002\u201d\u56e0\u4e3a\u6211\u77e5\u9053\uff0c\u53ea\u6709\u8036\u7a23\u4ed6\u7231\u6211\u5230\u5e95\uff0c\u4e5f\u6551\u6211\u5230\u5e95\u3002\u4ed6\u6551\u6211\u548c\u6211\u7684\u5168\u5bb6\uff0c\u4e3b\u554a\uff01\u6211\u613f\u5c0a\u4f60\u4e3a\u6211\u5bb6\u4e4b\u4e3b\uff01 \r\n</p>\r\n<p>\r\n\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0 \u6211\u4eec\u5bb6\u662f\u6211\u592a\u592a\u5148\u4fe1\u4e3b\uff0c\u5979\u4fe1\u4e3b\u8ddf\u6211\u6709\u5f88\u5927\u5173\u7cfb\u3002\u56e0\u4e3a\u6211\u7684\u539f\u56e0\u5f71\u54cd\u4e86\u6211\u5bb6\u5ead\u7684\u751f\u6d3b\uff0c\u5979\u77e5\u9053\u8036\u7a23\u6551\u5979\u4e5f\u6551\u6211\u3002\u5176\u5b9e\uff0c\u6211\u591a\u5e74\u524d\u5c31\u542c\u5230\u798f\u97f3\uff0c\u53ea\u662f\u4e0d\u4fe1\u4e5f\u4e0d\u660e\u767d\uff0c\u540c\u5927\u591a\u6570\u4eba\u4e00\u6837\u6211\u53ea\u76f8\u4fe1\u81ea\u5df1\u3002\u5e74\u8f7b\u65f6\u53ea\u8eab\u4ece\u91d1\u8fb9\u6765\u5230\u6cd5\u56fd\uff0c\u76f8\u4fe1\u4e16\u4e0a\u4e00\u5207\u4e8b\u53ea\u8981\u51ed\u81ea\u5df1\u7684\u529b\u6c14\u3001\u5934\u8111\u5c31\u53ef\u4ee5\u62e5\u6709\u4e00\u5207\u3002\u51e0\u5341\u5e74\u8fc7\u53bb\uff0c\u7ecf\u5386\u4e86\u79cd\u79cd\u8270\u96be\uff0c\u8f9b\u82e6\u505a\u5de5\u3002\u867d\u7136\u6709\u4e86\u7acb\u8eab\u4e4b\u5904\uff0c\u6709\u4e86\u5bb6\u5ead\u3001\u59bb\u5b50\u548c\u4e24\u4e2a\u53ef\u7231\u7684\u5973\u513f\u3002\u4f46\u4eba\u5fc3\u7684\u8d2a\u6b32\uff0c\u4e16\u4e0a\u7684\u8bf1\u60d1\u4f7f\u6211\u6c38\u4e0d\u6ee1\u8db3\uff0c\u6240\u62e5\u6709\u7684\u8fd9\u4e9b\u5e76\u4e0d\u770b\u91cd\uff0c\u603b\u60f3\u4eba\u751f\u5c31\u5f53\u4e00\u640f\u3002\u4ece\u5e74\u8f7b\u65f6\u5c31\u67d3\u4e0a\u4e86\u8d4c\u535a\u7684\u6076\u4e60\u3002\u9a6c\u573a\u3001\u8d4c\u573a\u51e0\u4e4e\u662f\u6211\u5de5\u4f59\u65f6\u95f4\u7684\u5168\u90e8\uff0c\u5c06\u81ea\u5df1\u8f9b\u82e6\u505a\u5de5\u7684\u94b1\u767d\u767d\u5730\u6295\u8fdb\u53bb\uff0c\u800c\u4e14\u8d8a\u9677\u8d8a\u6df1\uff0c\u771f\u7684\u662f\u8c61\u4eba\u8bf4\u7684\u662f\u201c\u8d4c\u5f92\u201d\u3002\u7ed3\u679c\u662f\u4eba\u53d8\u5f97\u813e\u6c14\u66b4\u8e81\uff0c\u5bb6\u4eba\u670b\u53cb\u7684\u529d\u662f\u542c\u4e0d\u8fdb\u7684\uff0c\u4f24\u5bb3\u4e86\u6211\u4eec\u592b\u59bb\u7684\u611f\u60c5\uff0c\u7834\u574f\u4e86\u5bb6\u5ead\u7684\u548c\u7766\u3002\u6709\u65f6\u5fc3\u91cc\u4e5f\u4e0d\u60f3\u8fd9\u6837\uff0c\u53ef\u8d4c\u535a\u662f\u9b54\u9b3c\u7684\u9677\u9631\uff0c\u4f7f\u4f60\u4e0d\u80fd\u6551\u81ea\u5df1\u3002\u592b\u59bb\u5e38\u5e38\u5435\u67b6\uff0c\u4ee4\u592a\u592a\u5341\u5206\u75db\u82e6\u5931\u671b\u3002\u5979\u5e38\u8bf4\uff1a\u201c\u751f\u6d3b\u600e\u6837\u82e6\u5979\u90fd\u4e0d\u6015\uff0c\u53ea\u8981\u8001\u516c\u80fd\u4e0d\u8d4c\uff0c\u8ba9\u5979\u505a\u4ec0\u4e48\u90fd\u884c\u3002\u201d\u6211\u5fc3\u786c\u5f97\u5f88\u542c\u4e0d\u8fdb\u4efb\u4f55\u529d\u544a\u3002 \r\n</p>\r\n<p>\r\n\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0 \u5f53\u6211\u7684\u5bb6\u5ead\uff0c\u592b\u59bb\u611f\u60c5\u8d8a\u6765\u8d8a\u5371\u9669\u65f6\u3002\u5929\u7236\u4e0a\u5e1d\u601c\u60af\u6211\u4eec\u4e00\u5bb6\u3002\u6709\u6559\u4f1a\u7684\u59d0\u59b9\u5e26\u6211\u592a\u592a\u8d70\u8fdb\u5df4\u9ece\u751f\u547d\u5802\uff0c\u63a5\u53d7\u4e86\u798f\u97f3\u3002\u4f7f\u5979\u660e\u767d\u4e3b\u8036\u7a23\u6551\u5979\uff0c\u4e5f\u6551\u6211\u548c\u6211\u7684\u5168\u5bb6\uff0c\u3002\u59bb\u5b50\u4fe1\u4e3b\u540e\uff0c\u6bcf\u4e2a\u661f\u671f\u5929\u5e26\u7740\u4e24\u4e2a\u5973\u513f\u53bb\u6559\u4f1a\u5f88\u662f\u5f00\u5fc3\u3002\u592a\u592a\u8ba9\u6211\u4e5f\u53bb\u6559\u4f1a\uff0c\u5e26\u6211\u53bb\u4fe1\u4e3b\u7684\u670b\u53cb\u5bb6\uff0c\u6559\u4f1a\u91cc\u7684\u4f20\u9053\u4eba\uff0c\u5f1f\u5144\u59d0\u59b9\u7ed9\u6211\u4f20\u798f\u97f3\uff0c\u5fc3\u786c\u7684\u6211\u542c\u4e0d\u8fdb\u53bb\uff0c\u6700\u7231\u53bb\u7684\u5730\u65b9\u8fd8\u662f\u8d4c\u573a\u3001\u8dd1\u9a6c\u573a\u2026\u2026\u2026\u3002 \r\n</p>\r\n<p>\r\n\u00a0\u00a0\u00a0\u00a0\u00a0 \u795e\u7684\u5e26\u9886\u771f\u7684\u5f88\u5947\u5999\uff0c2006\u5e745\u6708\uff0c\u59bb\u5b50\u548c\u6559\u4f1a\u4e2d\u7684\u59d0\u59b9\u7ea6\u6211\u53bb\u542c\u4e00\u573a\u9999\u6e2f\u827a\u4eba\u7684\u798f\u97f3\u4f1a\uff0c\u5f53\u65f6\u53ea\u662f\u60f3\u53bb\u770b\u660e\u661f\uff0c\u53ef\u542c\u8fc7\u4ed6\u4eec\u7684\u89c1\u8bc1\u5206\u4eab\u5341\u5206\u611f\u52a8\u3002\u660e\u767d\u4e86\u81ea\u5df1\u662f\u4e2a\u7f6a\u4eba\uff0c\u8036\u7a23\u662f\u6551\u7f6a\u4eba\u7684\u771f\u795e\u3002\u5f53\u7267\u5e08\u95ee\uff1a\u201c\u6709\u8c01\u613f\u610f\u63a5\u53d7\u8036\u7a23\u4f5c\u4ed6\u751f\u547d\u7684\u6551\u4e3b\u201d\u65f6\u3002\u6211\u9ad8\u9ad8\u5730\u4e3e\u8d77\u624b\uff0c\u4e0d\u7531\u81ea\u5df1\u7684\u4e3e\u8d77\u4e86\u624b\u3002\u7b2c\u4e8c\u5929\uff0c\u6211\u518d\u6b21\u53bb\u542c\u5e03\u9053\u4f1a\uff0c\u7267\u5e08\u89c1\u5230\u6211\u8bf4\uff1a\u201c\u6628\u5929\u662f\u4f60\u7b2c\u4e00\u4e2a\u4e3e\u624b\u63a5\u53d7\u8036\u7a23\u3002\u201d\u7267\u5e08\u53c8\u7ed9\u6211\u8bb2\u4e86\u8bb8\u591a\u5723\u7ecf\u91cc\u8036\u7a23\u6551\u7f6a\u4eba\u7684\u4e8b\uff0c\u5e76\u5e26\u6211\u505a\u4e86\u51b3\u5fd7\u7977\u544a\uff0c\u6211\u77e5\u9053\u4e86\u795e\u7231\u6211\u3001\u601c\u60af\u6211\u3002\u6211\u867d\u505a\u4e86\u8bb8\u591a\u795e\u4e0d\u559c\u60a6\u7684\u4e8b\uff0c\u4f46\u795e\u5bbd\u6055\u4e86\u6211\u30025\u6708\u5e95\u6211\u5728\u5df4\u9ece\u751f\u547d\u5802\u63a5\u53d7\u4e86\u6d17\u793c\uff0c\u6211\u7684\u4e24\u4e2a\u5973\u513f\u4e5f\u4f5c\u4e86\u5949\u732e\u793c\uff0c\u6211\u4eec\u5bb6\u6210\u4e3a\u4e86\u4e00\u4e2a\u57fa\u7763\u5f92\u5bb6\u5ead\u3002\u7267\u5e08\u3001\u4f20\u9053\u4eba\u7ed9\u6211\u8bb2\u4e86\u5f88\u591a\u9053\u7406\uff0c\u544a\u8bc9\u6211\u6700\u8981\u7d27\u7684\u4e8b\u662f\u81ea\u5df1\u8981\u7acb\u5fd7\u6094\u6539\uff0c\u8fdc\u79bb\u6076\u4e8b\u3002\u6211\u51b3\u5fc3\u6212\u8d4c\uff0c\u6c42\u4e3b\u5e2e\u52a9\u3002\u6700\u5f00\u5fc3\u7684\u662f\u6211\u592a\u592a\uff0c\u56e0\u4e3a\u5979\u77e5\u9053\u53ea\u6709\u8036\u7a23\u80fd\u6551\u6211\uff0c\u50cf\u90a3\u6b4c\u91cc\u5531\u7684\u201c\u524d\u6211\u5931\u4e27\uff0c\u4eca\u88ab\u5bfb\u56de\u2026\u2026.\u201d \r\n</p>\r\n<p>\r\n\u00a0\u00a0\u00a0\u00a0\u00a0 \u4fe1\u4e3b\u540e\u7684\u65e5\u5b50\u5f88\u5f00\u5fc3\uff0c\u5df4\u9ece\u751f\u547d\u5802\u6210\u4e3a\u6211\u5168\u5bb6\u7684\u4e00\u4e2a\u5c5e\u7075\u7684\u65b0\u5bb6\u3002\u6211\u4e0d\u518d\u53bb\u8d4c\u573a\uff0c\u53ea\u8981\u6709\u65f6\u95f4\u5c31\u53bb\u6559\u4f1a\u53c2\u52a0\u67e5\u7ecf\uff0c\u6bcf\u5230\u4e3b\u65e5\uff0c\u5168\u5bb6\u65e9\u65e9\u53bb\u6559\u4f1a\u805a\u4f1a\uff0c\u656c\u62dc\u795e\u3002\u5973\u513f\u5728\u4e3b\u65e5\u5b66\u91cc\u5b66\u5723\u7ecf\uff0c\u5b66\u8bd7\u6b4c\uff0c\u5b66\u4e2d\u6587\u3002\u6211\u7684\u4e2d\u6587\u4f1a\u7684\u4e0d\u591a\uff0c\u4f20\u9053\u4eba\uff0c\u5f1f\u5144\u59d0\u59b9\u90fd\u6765\u5e2e\u52a9\u6211\uff0c\u4e3a\u8981\u8ba9\u6211\u660e\u767d\u66f4\u591a\u795e\u7684\u9053\u7406\u3002\u6211\u6700\u5148\u4f1a\u88ab\u7684\u7ecf\u6587\u662f\u7ea6\u7ff0\u798f\u97f3\u5341\u56db\u7ae0\u516d\u8282\u3002\u8036\u7a23\u8bf4\uff1a\u201c\u6211\u5c31\u662f\u9053\u8def\u3001\u771f\u7406\u3001\u751f\u547d\uff1a\u82e5\u4e0d\u501f\u7740\u6211\uff0c\u6ca1\u6709\u4eba\u80fd\u5230\u7236\u90a3\u91cc\u53bb\u3002\u201d\u5f53\u6211\u7684\u8001\u677f\u548c\u522b\u4eba\u5728\u6211\u9762\u524d\u5938\u8000\u6709\u8fd9\u4e2a\u540d\u8f66\u90a3\u4e2a\u540d\u724c\u65f6\uff0c\u4ee5\u524d\u6211\u5fc3\u91cc\u4f1a\u5ac9\u5992\u6124\u6124\u4e0d\u5e73\uff0c\u4f46\u4eca\u5929\u6211\u5e38\u5e38\u544a\u8bc9\u4ed6\u4eec\uff1a\u201c\u6211\u6709\u8036\u7a23\uff0c\u4ed6\u7ed9\u6211\u7684\u6bd4\u4e16\u4e0a\u7684\u4e00\u5207\u90fd\u597d\u201d\u3002\u5728\u5de5\u4f5c\u65f6\u6309\u4ee5\u524d\u7684\u813e\u6c14\uff0c\u5e38\u548c\u522b\u4eba\u4e89\u6267\uff0c\u73b0\u5728\u6211\u8981\u6539\u53d8\uff0c\u4e0d\u548c\u4ed6\u4eec\u4e89\u5435\uff0c\u505a\u597d\u81ea\u5df1\u8be5\u505a\u7684\u4e8b\u3002\u6211\u7684\u5bb6\u5ead\u4e5f\u4e0d\u518d\u4e89\u5435\uff0c\u548c\u7766\u6e29\u6696\uff0c\u56e0\u4e3a\u6211\u611f\u53d7\u5230\u4e3b\u7684\u7231\u548c\u5f1f\u5144\u59d0\u59b9\u7684\u7231\u3002\u6211\u4e5f\u613f\u610f\u548c\u5f1f\u5144\u59d0\u59b9\u5206\u4eab\u4e3b\u7684\u7231\u3002\u613f\u610f\u4e3a\u6559\u4f1a\uff0c\u4e3a\u5927\u5bb6\u505a\u81ea\u5df1\u80fd\u505a\u7684\u4e8b\uff0c\u6211\u77e5\u9053\u8fd9\u662f\u4e8b\u5949\u4e3b\uff0c\u6240\u4ee5\u5e38\u5e38\u5f00\u653e\u6211\u7684\u5bb6\u5ead\uff0c\u5f1f\u5144\u59d0\u59b9\u5e38\u5728\u4e00\u8d77\u805a\u4f1a\u3001\u67e5\u7ecf\u3001\u8d5e\u7f8e\u795e\u3002\u795e\u4f7f\u6211\u4eec\u4e00\u5bb6\u5f97\u5e73\u5b89\uff0c\u795e\u7684\u770b\u987e\u662f\u5b9e\u5b9e\u5728\u5728\u7684\uff0c\u771f\u7684\u611f\u8c22\u4e3b\u3002 \r\n</p>\r\n<p>\r\n\u00a0\u00a0\u00a0\u00a0\u00a0\u00a02006\u5e74\u590f\u5929\uff0c\u6211\u56de\u91d1\u8fb9\u63a2\u671b\u7236\u6bcd\u5bb6\u4eba\uff0c\u5f53\u6211\u544a\u8bc9\u4ed6\u4eec\u6211\u4fe1\u4e3b\u6210\u4e3a\u57fa\u7763\u5f92\u4e0d\u518d\u8d4c\u9a6c\u65f6\uff0c\u5168\u5bb6\u4eba\u90fd\u4e3a\u6211\u9ad8\u5174\uff0c\u4e00\u4f4d\u4eb2\u621a\u9001\u6211\u4e00\u672c\u91d1\u8fb9\u6587\u5723\u7ecf\uff0c\u4e3a\u80fd\u66f4\u597d\u7684\u5e2e\u52a9\u6211\u5b66\u4e60\u5723\u7ecf\u3002\u5728\u6211\u4fe1\u4e3b\u4e00\u6bb5\u65f6\u95f4\u540e\uff0c\u9b54\u9b3c\u6492\u65e6\u53c8\u6765\u653b\u51fb\u6211\uff0c\u81ea\u5df1\u8f6f\u5f31\u4e0b\u6765\uff0c\u5fc3\u53c8\u88ab\u4e16\u754c\u7684\u8d25\u574f\u5f15\u8bf1\uff0c\u5c06\u4e3b\u7684\u8bdd\u5fd8\u5230\u8111\u540e\uff0c\u5bf9\u6559\u4f1a\u7684\u4e8b\u4e0d\u518d\u70ed\u5fc3\uff0c\u53c8\u56de\u5230\u4e86\u4ece\u524d\uff0c\u53bb\u8dd1\u9a6c\u573a\u8d4c\u9a6c\uff0c\u53bb\u8d4c\u573a\u73a9\u3002\u59bb\u5b50\u548c\u5f1f\u5144\u59d0\u59b9\u90fd\u5f88\u7740\u6025\uff0c\u5929\u5929\u4e3a\u6211\u7977\u544a\uff0c\u4e5f\u6709\u4e00\u4e9b\u4eba\u5728\u8bb2\u4e09\u8bb2\u56db\u770b\u6211\u7684\u7b11\u8bdd\uff0c\u4e00\u6bb5\u65f6\u95f4\u540e\u81ea\u5df1\u611f\u5230\u5fc3\u91cc\u6ca1\u6709\u5e73\u5b89\uff0c\u5f88\u7d2f\u5f88\u70e6\uff0c\u53c8\u6b20\u4e86\u8d4c\u503a\u3002\u65e0\u52a9\u7684\u65f6\u5019\u6211\u60f3\u5230\u4e3b\u8036\u7a23\u3002\u6211\u7977\u544a\uff1a\u201c\u6c42\u4e3b\u6551\u6211\uff0c\u6211\u4e0d\u60f3\u8d4c\u4e86\uff0c\u6211\u9519\u4e86\uff0c\u9760\u6211\u81ea\u5df1\u4e0d\u80fd\u3002\u4e3b\u554a\uff0c\u4f60\u80fd\u6551\u6211\uff0c\u6211\u5f97\u7f6a\u4e86\u4f60\uff0c\u6c42\u4f60\u53ef\u601c\u6211\uff0c\u6551\u6211\u3002\u201d\u4f20\u9053\u4eba\u540c\u6211\u8c08\u8bdd\uff0c\u6211\u77e5\u9053\u81ea\u5df1\u6240\u72af\u7684\u9519\u613f\u610f\u5728\u4e3b\u9762\u524d\u8ba4\u7f6a\u3001\u6094\u6539\u3002\u611f\u8c22\u4e3b\uff01\u4f60\u8bf4\u8fc7\u201c\u6211\u603b\u90e8\u6487\u4e0b\u4f60\uff0c\u4e5f\u4e0d\u4e22\u5f03\u4f60\u3002\u201d\u4f60\u53c8\u4e00\u6b21\u5c06\u6211\u4ece\u7f6a\u4e2d\u6551\u51fa\u3002\u4f60\u5947\u5f02\u6069\u5178\u7684\u7518\u751c\uff0c\u4f7f\u6211\u5fc3\u7684\u5b89\u6170\u3002\u91cd\u65b0\u8d70\u51fa\u9ed1\u6697\u771f\u89c9\u5f97\u4ece\u5fc3\u91cc\u8f7b\u677e\u3001\u559c\u4e50\u3001\u5e73\u5b89\u3002\u6211\u60f3\u8d77\u6625\u8282\u65f6\u5f97\u5230\u7684\u5723\u7ecf\u91d1\u53e5\uff0c\u4e3b\u5bf9\u6211\u8bf4\uff1a\u201c\u4e0d\u8981\u7231\u4e16\u754c\u548c\u4e16\u754c\u4e0a\u7684\u4e8b\uff1b\u4eba\u82e5\u7231\u4e16\u754c\uff0c\u7231\u7236\u7684\u5fc3\u5c31\u4e0d\u5728\u4ed6\u91cc\u9762\u4e86\u3002\u201d\uff08\u7ea6\u4e002\uff1a15\uff09\u6c42\u4e3b\u8d50\u6211\u7231\u4e3b\u7684\u5fc3\uff0c\u8ba9\u6211\u5c06\u4e3b\u7684\u8bdd\u8bb0\u5728\u5fc3\u91cc\u3002 \r\n</p>\r\n<p>\r\n\u00a0\u00a0\u00a0\u00a0\u00a0 \u4fe1\u4e3b\u4e00\u5e74\u591a\u6765\u6211\u548c\u6211\u7684\u5168\u5bb6\u5f97\u7740\u592a\u591a\u4e3b\u7684\u6069\u5178\uff0c\u5df4\u9ece\u751f\u547d\u5802\u7684\u4f20\u9053\u4eba\uff0c\u5f1f\u5144\u59d0\u59b9\u7ed9\u4e86\u6211\u5f88\u591a\u5e2e\u52a9\u3002\u662f\u8036\u7a23\u7684\u7231\u4f7f\u6211\u4eec\u5f7c\u6b64\u76f8\u7231\u3002\u751f\u547d\u5802\u662f\u6211\u7684\u5bb6\uff0c\u6709\u6211\u7684\u8d23\u4efb\u3002\u6211\u548c\u6211\u5168\u5bb6\u613f\u5c0a\u8036\u7a23\u4e3a\u6211\u5bb6\u4e4b\u4e3b\uff0c\u613f\u4e00\u751f\u4e8b\u5949\u771f\u795e\u4e0a\u5e1d\u3001\u4e8b\u5949\u7231\u6211\u4eec\u7684\u4e3b\u3002\u6211\u613f\u544a\u8bc9\u6240\u6709\u7684\u4eba\uff0c\u6211\u6709\u8036\u7a23\uff0c\u4ed6\u7231\u6211\uff0c\u6551\u6211\u3002\u4e5f\u7231\u4e16\u4e0a\u6bcf\u4e00\u4e2a\u4eba\uff0c\u6551\u613f\u610f\u76f8\u4fe1\u4ed6\u7684\u6bcf\u4e00\u4e2a\u4eba\u3002 \r\n</p>\r\n<p>\r\n\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u00a0\u9673\u6b66\u5f1f\u5144\u00a0\u00a0 \u00a02007\u5e749\u67083\u65e5 \r\n</p>\r\n<p>\r\n&nbsp;\r\n</p>\r\n<p>\r\n\u5728\u4e8b